package com.pofs.metiers;import com.pofs.ProductsFragment;public class Products {private int code;private float price;private String name;private String family;private int image;public int getCode() {	return code;}public void setCode(int code) {	this.code = code;}public float getPrice() {	return price;}public void setPrice(float price) {	this.price = price;}public String getName() {	return name;}public void setName(String name) {	this.name = name;}public String getFamily() {	return family;}public void setFamily(String family) {	this.family = family;}public int getImage() {	return image;}public void setImage(int image) {	this.image = image;}}